mapper.select:
        select max(h_date)
		from history
		where ticker = #{value}

mapper.insert:
insert into history 
(
	ticker
	, h_date
	, open_price
	, high_price
	, low_price
	, close_price
	, h_change
) 
values 

(
	#{item.ticker}
	, #{item.date}
	, #{item.openPrice}
	, #{item.highPrice}
	, #{item.lowPrice}
	, #{item.closePrice}
	, #{item.change}
)

insert into buy_ticker 
(
	seq
	, b_date
	, ticker
	, weight
	, best_length
	, best_percent
	, average_by_plus
	, average_by_minus
) 
values 

(
	#{item.seq}
	, #{item.date}
	, #{item.ticker}
	, #{item.weight}
	, #{item.bestLength}
	, #{item.bestPercent}
	, #{item.averageByPlus}
	, #{item.averageByMinus}
)

insert into buy_ticker 
(
	seq
	, subSeq
	, profitAndLoss
	, avgProfitAndLossByMonth
	, avgProfitAndLossByYear
	, avgSuccessPercent
	, avgSuccessChange
	, avgFailPercent
	, avgFailChange
	, jsonStr
) 
values 
(
	#{seq}
	, #{subSeq}
	, #{profitAndLoss}
	, #{avgProfitAndLossByMonth}
	, #{avgProfitAndLossByYear}
	, #{avgSuccessPercent}
	, #{avgSuccessChange}
	, #{avgFailPercent}
	, #{avgFailChange}
	, #{jsonStr}
)
